import React from "react";
import useSWR from "swr";

const BestPost = () => {
  const [pageIndex, setPageIndex] = useState(1);
  const { data } = useSWR("http://localhost:8000/bestpost");
  return (
    <div className="container">
      <div>
        <h1>welcome to best post page</h1>
      </div>
      <table className="table table-striped table-hover">
        <thead>
          <tr>
            <th>index</th>
            <th>title</th>
            <th>email</th>
            <th>text</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr>
              <td>{index + 1}</td>
              <td>{item.title}</td>
              <td>{item.email}</td>
              <td>{item.text}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BestPost;
