import React from "react";
import { Col, Container, Row } from "react-bootstrap";
import SliderComponent from "../../components/slider/Slider";
import axios from "axios";
import useSWR from "swr";

const fetcher = (args) => axios.get(args).then((res) => res.data);

function LandingPage() {
  const { data: sliderData, error: sliderError } = useSWR(
    "http://localhost:8000/slider",
    fetcher
  );
  const { data, error } = useSWR("http://localhost:8000/lastpost", fetcher);
  if (error) return <h1>{error.message}</h1>;
  if (!data) return <h1>Loading ...</h1>;
  return (
    <Container className="mt-3">
      <Row>
        <Col xs={4}>
          <SliderComponent data={sliderData} />
        </Col>
        <Col xs={8}>
          Lorem ipsum dolor sit, amet consectetur adipisicing elit. Hic sed iste
          atque, laborum libero qui magnam necessitatibus nulla vitae quia
          impedit voluptatum quam nisi amet excepturi repellat dicta est sequi.
        </Col>
      </Row>
      <Row className="mt-4">
        <Col xs={12}>
          <div className="d-flex justify-content-between">
            <h3>Last Post</h3>
            <button className="btn btn-outline-secondary">More ...</button>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default LandingPage;
