import React from "react";
import { Col, Container, Row } from "react-bootstrap";
import SliderComponent from "../../components/slider/Slider";
import axios from "axios";
import useSWR from "swr";
import ShowPostSlider from "../../components/landingComponent/showpostslider/ShowPost";

function LandingPage() {
  const { data: sliderData, error: sliderError } = useSWR(
    "http://localhost:8000/slider",
    {
      revalidateOnFocus: false,
      errorRetryInterval: 5000,
    }
  );

  //   last post req
  const { data: lastPostData, error: lastPostError } = useSWR(
    "http://localhost:8000/lastpost",
    {
      revalidateOnFocus: false,
      errorRetryInterval: 5000,
    }
  );

  // best post req
  const { data: bestPostData, error: bestPostError } = useSWR(
    "http://localhost:8000/bestpost",
    {
      revalidateOnFocus: false,
      errorRetryInterval: 5000,
    }
  );

  if (sliderError || lastPostError || bestPostError)
    return <h1>{sliderError.message}</h1>;
  if (!sliderData || !lastPostData || !bestPostData)
    return <h1>Loading ...</h1>;

  return (
    <Container className="mt-3">
      <Row>
        <Col xs={4}>
          <SliderComponent data={sliderData} />
        </Col>
        <Col xs={8}>
          Lorem ipsum dolor sit, amet consectetur adipisicing elit. Hic sed iste
          atque, laborum libero qui magnam necessitatibus nulla vitae quia
          impedit voluptatum quam nisi amet excepturi repellat dicta est sequi.
        </Col>
      </Row>
      <Row className="my-4">
        <Col xs={12}>
          <div className="d-flex justify-content-between">
            <h3>Last Post</h3>
            <button className="btn btn-outline-secondary">More ...</button>
          </div>
        </Col>
        <Col xs={12}>
          <ShowPostSlider data={lastPostData} />
        </Col>
      </Row>
      {/* best posts */}
      <Row className="my-4">
        <Col xs={12}>
          <div className="d-flex justify-content-between">
            <h3>Best Post</h3>
            <button className="btn btn-outline-secondary">More ...</button>
          </div>
        </Col>
        <Col xs={12}>
          <ShowPostSlider data={bestPostData} />
        </Col>
      </Row>
    </Container>
  );
}

export default LandingPage;
