import React from "react";
import { Card, Col } from "react-bootstrap";

function LastPostSlider({ data }) {
  return (
    <>
      {data.map((item) => (
        <Col xs={3} key={item.id}>
          <Card className="border-3 shadow-lg">
            <Card.Body>
              <Card.Title>{item.title}</Card.Title>
              <Card.Text>
                <p>{item.email}</p>
                <p>{item.text}</p>
              </Card.Text>
            </Card.Body>
            <Card.Footer>
              <p>{item.date}</p>
            </Card.Footer>
          </Card>
        </Col>
      ))}
    </>
  );
}

export default LastPostSlider;
